document.querySelectorAll(".navbar__menu a").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});let navbarToggle=document.getElementById("navbarToggle"),navbarMenu=document.getElementById("navbarMenu");navbarToggle.addEventListener("click",()=>{navbarMenu.classList.toggle("navbar__menu--active")});